import React from "react";
import Popup from 'reactjs-popup';
import 'reactjs-popup/dist/index.css';


// reactstrap components
import {
  Badge,
  Card,
  CardHeader,
  CardFooter,
  DropdownMenu,
  Button,
  DropdownItem,
  UncontrolledDropdown,
  DropdownToggle,
  Media,
  Pagination,
  PaginationItem,
  Form,
  FormGroup,
  InputGroupAddon,
  InputGroupText,
  Input,
  InputGroup,
  Navbar,
  Nav,
  PaginationLink,
  Progress,
  Table,
  Container,
  Row,
  UncontrolledTooltip,
} from "reactstrap";

// core components
import TrendsHeader from "components/Headers/TrendsHeader.js";
import Login from './examples/googleLogin';
import {Redirect} from 'react-router-dom';

var trendsData = {};

class TiktokTrends extends React.Component {

  constructor() {
    super();
    this.state = {
      hashtags: [],
      musics: [],
      topMusic: {info: {title: "--"}, musicDesc: ""},
      topHashtag: {info: {title: "--"}, viewDesc: ""}
    };
  }

  onSubmit = () => {
      this.props.history.push('/login/');
  }

  componentDidMount() {
    fetch('http://ec2-18-117-197-197.us-east-2.compute.amazonaws.com:8100/discover')
      .then((response) => response.json())
      .then((responseJson) => {
        this.setState({
          hashtags: responseJson.data.hashtags,
          musics: responseJson.data.musics,
          topMusic: responseJson.data.top_music,
          topHashtag: responseJson.data.top_hashtag});
      })
      .catch((error) => {
        console.error(error);
      });
  }

  copyClicked(text) {
    alert("Item copied " + text);
  }

  render() {

    const modal = {
        fontSize: '12px'
    };



    const modalContent = {
      width: '100%',
      padding: '10px 5px'
    }

    const modalActions = {
      width: '100%',
      padding: '10px 5px',
      margin: 'auto',
      textAlign: 'center'
    }

    const modalClose = {
      cursor: 'pointer',
      position: 'absolute',
      display: 'block',
      padding: '2px 5px',
      lineHeight: '20px',
      right: '-10px',
      top: '-10px',
      fontSize: '24px',
      background: '#ffffff',
      borderRadius: '18px',
      border: '1px solid #cfcece'
    }

    return (
      <>

      <div className="pt-7">
          <Container fluid className="header pt-8">
              <Container className="mt--7"  fluid>
                <Row>
                  <div className="col">
                    <Card className="shadow">
                      <Row style={{ textAlign: 'center', paddingTop: "20px", paddingLeft: "20px", paddingRight: "20px"}}>
                        <h2 className=" text-muted " style={{paddingLeft: "20px"}}> Your Watchlist </h2>
                        <div className="col text-right" style={{paddingBottom:"2%"}}>
                            <Popup
                              trigger={ <Button color="primary" size="sm"> Add User </Button>}
                              modal
                              nested
                            >
                                {close => (
                                <div style={modal}>
                                  <button className="close" onClick={close}>
                                     &times;
                                  </button>
                                  <div style={modalContent}  className="content">
                                    <form onSubmit={this.attachTiktokUsernameHandler}>
                                      <h2>Enter a tiktok username to watch</h2>
                                      <br />
                                      <input
                                        type='text'
                                        onChange={this.tiktokInputChangeHandler}
                                        placeholder ="Enter Tiktok username"
                                        style={{border: "1px lightgray solid", borderRadius: "15px", padding: "5px 45px", color:"lightgray", marginLeft: "50px"}}
                                      />
                                      <input
                                        type='submit'
                                        style={{color: "#fff", backgroundColor: "rgb(80 80 80)", border: "0px lightgray solid", marginLeft: "10px", borderRadius: "30px", padding: "5px 15px" }}
                                      />
                                    </form>

                                  </div>
                                  <div style={modalActions} className="actions">
                                    <Popup
                                      trigger={<button className="button"> Trigger </button>}
                                      position="top center"
                                      nested
                                    >
                                    </Popup>
                                  </div>
                                </div>
                              )}
                           </Popup>
                        </div>
                      </Row>
                      <Table className="align-items-center table-flush" responsive>
                        <thead className="thead-light">
                          <tr>
                            <th scope="col">Item</th>
                            <th scope="col">Number of Views</th>
                            <th scope="col">Description</th>
                            <th scope="col">Link</th>
                            <th scope="col" />
                          </tr>
                        </thead>
                        <tbody>
                        </tbody>
                      </Table>
                    </Card>
                  </div>
                </Row>
              </Container>
            </Container>
        </div>

      </>
    );
  }
}

export default TiktokTrends;
