import React from "react";

// reactstrap components
import {
  Badge,
  Card,
  CardHeader,
  CardFooter,
  DropdownMenu,
  DropdownItem,
  UncontrolledDropdown,
  DropdownToggle,
  Media,
  Pagination,
  PaginationItem,
  PaginationLink,
  Progress,
  Table,
  Container,
  Row,
  UncontrolledTooltip
} from "reactstrap";

// core components
import Header from "components/Headers/Header.js";

var trendsData = {};

class TiktokTrends extends React.Component {

  constructor() {
    super();
    this.state = {
      hashtags: [],
      musics: [],
    };
  }

  componentDidMount() {
    fetch('http://ec2-18-117-221-208.us-east-2.compute.amazonaws.com:8100/discover')
      .then((response) => response.json())
      .then((responseJson) => {
        console.log(responseJson);
        this.setState({ hashtags: responseJson.data.hashtags, musics: responseJson.data.musics});
        console.log(this.state);
      })
      .catch((error) => {
        console.error(error);
      });
  }

  copyClicked(text) {
    alert("Item copied " + text);
  }

  render() {
    let hashtagItems = this.state.hashtags.map((tag, index) => {
      return (
        <tr key={tag.id}>
          <th scope="row"><Media><span>{tag.info.title}</span></Media></th>
          <td>{tag.viewDesc}</td>
          <td style={{width: '200px'}}><a href={tag.info.link} _blank="true">{tag.info.link}</a></td>
          <td>{tag.info.description}</td>
          <td><a onClick={this.copyClicked.bind(this, tag.info.title)}><i className="fas fa-copy"></i></a></td>
        </tr>
      );
    });

    let musicItems = this.state.musics.map((item, index) => {
      return (
        <tr key={item.id}>
          <th scope="row">
            <Media className="align-items-center">
              <a className="avatar rounded-circle mr-3"><img alt="" src={item.info.thumbnail} /></a>
              <Media><span>{item.info.title}</span></Media>
            </Media>
          </th>
          <td>{item.musicDesc}</td>
          <td><a href={item.info.link} _blank="true">{item.info.link}</a></td>
          <td>{item.info.description}</td>
          <td><a onClick={this.copyClicked.bind(this, item.info.title)}><i className="fas fa-copy"></i></a></td>
        </tr>
      );
    });

    return (
      <>
        <Header />
        <Container className="mt--7" fluid>
          <Row>
            <div className="col">
              <Card className="shadow">
                <CardHeader className="border-0">
                  <h3 className="mb-0">Top Hashtags</h3>
                </CardHeader>
                <Table className="align-items-center table-flush" responsive>
                  <thead className="thead-light">
                    <tr>
                      <th scope="col">Hashtag</th>
                      <th scope="col"># of Views</th>
                      <th scope="col">Link</th>
                      <th scope="col">Description</th>
                      <th scope="col" />
                    </tr>
                  </thead>
                  <tbody>
                    {hashtagItems}
                  </tbody>
                </Table>
              </Card>
            </div>
          </Row>
          {/* Dark table */}
          <Row className="mt-5">
            <div className="col">
              <Card className="bg-default shadow">
                <CardHeader className="bg-transparent border-0">
                  <h3 className="text-white mb-0">Top Songs</h3>
                </CardHeader>
                <Table
                  className="align-items-center table-dark table-flush"
                  responsive
                >
                  <thead className="thead-dark">
                    <tr>
                      <th scope="col">Song Title</th>
                      <th scope="col"># of Vidoes</th>
                      <th scope="col">Link</th>
                      <th scope="col">Description</th>
                      <th scope="col" />
                    </tr>
                  </thead>
                  <tbody>
                    {musicItems}
                  </tbody>
                </Table>
              </Card>
            </div>
          </Row>
        </Container>
      </>
    );
  }
}

export default TiktokTrends;
