import React from 'react';
import {PricingTable, PricingSlot, PricingDetail} from 'react-pricing-table';
import Stripe from './Stripe.js';
import {loadStripe} from '@stripe/stripe-js';
import {
  CardElement,
  Elements,
  useStripe,
  useElements,
} from '@stripe/react-stripe-js';
const stripePromise = loadStripe('pk_test_6pRNASCoBOKtIshFeQd4XMUh');

export default class Pricing extends React.Component {
  handleSubmit = async (event) => {
   event.preventDefault();
   const {stripe, elements} = this.props;

   if (elements == null) {
     return;
   }

   const {error, paymentMethod} = await stripe.createPaymentMethod({
     type: 'card',
     card: elements.getElement(CardElement),
   });
 };

  constructor(props) {
    super(props);
    this.tooltipRef = React.createRef();
  }

  render() {
    const {
      text,
      tooltipText,
      placement,
      textClass,
      tooltipClass,
    } = this.props;

    const {stripe} = this.props;

    return (

      <div className="pt-8" style={{marginTop:"3%"}}>
        <PricingTable highlightColor='#6074DA'>
            <PricingSlot style={{borderRadius:"5%"}} onClick={this.submit} buttonText='TRY IT FREE' title='Free' priceText='$0/month'>
                <PricingDetail> <b>15</b> projects</PricingDetail>
                <PricingDetail> <b>5 GB</b> storage</PricingDetail>
                <PricingDetail> <b>5</b> users</PricingDetail>
                <PricingDetail strikethrough> <b>Time tracking</b></PricingDetail>
            </PricingSlot>
            <PricingSlot highlighted onClick={this.submit} buttonText='SIGN UP' title='Creator Pro' priceText='$15/month'>
                <PricingDetail> <b>35</b> projects</PricingDetail>
                <PricingDetail> <b>15 GB</b> storage</PricingDetail>
                <PricingDetail> <b>Unlimited</b> users</PricingDetail>
                <PricingDetail> <b>Time tracking</b></PricingDetail>
            </PricingSlot>
            <PricingSlot  onClick={this.submit} buttonText='SIGN UP' title='Brand' priceText='$99/month'>
                <PricingDetail> <b>100</b> projects</PricingDetail>
                <PricingDetail> <b>30 GB</b> storage</PricingDetail>
                <PricingDetail> <b>Unlimited</b> users</PricingDetail>
                <PricingDetail> <b>Time tracking</b></PricingDetail>
            </PricingSlot>
        </PricingTable>

        <form onSubmit={this.handleSubmit}>
         <CardElement />
         <button type="submit" disabled={!stripe}>
           Pay
         </button>
       </form>
      </div>

    );
  }
}
