import React from "react";
import Popup from 'reactjs-popup';
import 'reactjs-popup/dist/index.css';

// reactstrap components
import {
  Badge,
  Card,
  CardHeader,
  CardFooter,
  DropdownMenu,
  Button,
  DropdownItem,
  UncontrolledDropdown,
  DropdownToggle,
  Media,
  Pagination,
  PaginationItem,
  Form,
  FormGroup,
  InputGroupAddon,
  InputGroupText,
  Input,
  InputGroup,
  Navbar,
  Nav,
  PaginationLink,
  Progress,
  Table,
  Container,
  Row,
  UncontrolledTooltip,
} from "reactstrap";

// core components
import TrendsHeader from "components/Headers/TrendsHeader.js";
import Login from './examples/googleLogin';
import {Redirect} from 'react-router-dom';
import PopUpPay from './examples/PopUpPay.js'

var trendsData = {};

class TiktokTrends extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      username: '',
      userData: [],
      showPayPopup: false
    };
  }

  attachTiktokUsernameHandler = (event) => {
    event.preventDefault();
    alert("You are submitting " + this.state.username);

    var body = {};
    body.username = this.state.username;
    body.userId = localStorage.getItem("reveleUserID");

    fetch('http://localhost:5100/api/users/' + localStorage.getItem("reveleUserID") + '/watch/users', {
      method: 'post',
      body: JSON.stringify(body),
      headers: {'Content-Type': 'application/json'}
    }).then((response) => response.json())
      .then((responseJson) => {
        if (responseJson.success == true) {
          window.location.href.reload();
        } else {
          // alert("There is some issue in Google Login at this time. Please try again later.");
        }
      })
      .catch((error) => {
        console.error(error);
        alert("Catch There is some issue in Google Login at this time. Please try again later.");
      });
  }

  componentDidMount() {
    fetch('http://localhost:5100/api/users/5577fc52-5787-4708-ab77-26bc4/watchlist')
      .then((response) => response.json())
      .then((responseJson) => {
        console.log(responseJson);
        this.setState({
          userData: responseJson.data ? responseJson.data.users : [],
          showPayPopup: responseJson.response_code == 400 ? true : false
        });

      })
      .catch((error) => {
        console.error(error);
      });
  }

  tiktokInputChangeHandler = (event) => {
    this.setState({username: event.target.value});
  }

  render() {

    const modal = {
        fontSize: '12px'
    };

    const modalContent = {
      width: '100%',
      padding: '20px',
      textAlign: 'center'
    }

    const modalActions = {
      width: '100%',
      padding: '10px 5px',
      margin: 'auto',
      textAlign: 'center'
    }

    const modalClose = {
      cursor: 'pointer',
      position: 'absolute',
      display: 'block',
      padding: '2px 5px',
      lineHeight: '20px',
      right: '-10px',
      top: '-10px',
      fontSize: '24px',
      background: '#ffffff',
      borderRadius: '18px',
      border: '1px solid #cfcece'
    }

    let userItems = this.state.userData.map((tag, index) => {
      return (
          <tr key={tag.id}>
            <th scope="row">
              <Media className="align-items-center">
                <a className="avatar rounded-circle mr-3" target="_blank"><img alt="" src={tag.image} style={{borderRadius: 0}} /></a>
              </Media>
            </th>
            <td>{tag.username}</td>
            <td>{tag.followerCount}</td>
            <td>{tag.totalPlayCount}</td>
            <td>{tag.totalShareCount}</td>
            <td>{tag.totalCommentCount}</td>
        </tr>
        );
    });

    return (
      <>
      {this.state.showPayPopup ? <PopUpPay /> : "" }

      <div className="pt-7">
          <Container fluid className="header pt-8">
              <Container className="mt--7"  fluid>
                <Row>
                  <div className="col">
                    <Card className="shadow">
                      <Row style={{ textAlign: 'center', paddingTop: "20px", paddingLeft: "20px", paddingRight: "20px"}}>
                        <h2 className=" text-muted " style={{paddingLeft: "20px"}}> Your User Watchlist </h2>
                        <div className="col text-right" style={{paddingBottom:"2%"}}>
                            <Popup
                              trigger={ <Button color="primary" size="sm"> Add User </Button>}
                              modal
                              nested
                              style={{width:'50%'}}
                            >
                                {close => (
                                <div style={modal}>
                                  <button className="close" onClick={close}>
                                     &times;
                                  </button>
                                  <div style={modalContent}  className="content">
                                    <form onSubmit={this.attachTiktokUsernameHandler}>
                                      <h2>Enter a tiktok username to watch</h2>
                                      <br />
                                      <input
                                        type='text'
                                        onChange={this.tiktokInputChangeHandler}
                                        placeholder ="Enter Tiktok username"
                                        style={{border: "1px lightgray solid", borderRadius: "15px", padding: "5px 45px", color:"lightgray", marginLeft: "50px"}}
                                      />
                                      <input
                                        type='submit'
                                        style={{color: "#fff", backgroundColor: "rgb(80 80 80)", border: "0px lightgray solid", marginLeft: "10px", borderRadius: "30px", padding: "5px 15px" }}
                                      />
                                    </form>
                                  </div>
                                </div>
                              )}
                           </Popup>
                        </div>
                      </Row>
                      <Table className="align-items-center table-flush" responsive>
                        <thead className="thead-light">
                          <tr>
                            <th scope="col">Username</th>
                            <th scope="col">FollowerCount</th>
                            <th scope="col">Total Views</th>
                            <th scope="col">Views </th>
                            <th scope="col">Shares</th>
                            <th scope="col">Comments </th>
                          </tr>
                        </thead>
                        <tbody>
                        {userItems}
                        </tbody>
                      </Table>
                    </Card>
                  </div>
                </Row>

                <Row>
                  <div className="col pt-3">
                    <Card className="shadow">
